for(var buyButtons=document.querySelectorAll(".buy"),buyPopup=document.querySelector(".modal-product-added"),buyClose=document.querySelector(".modal-product-added .modal-close"),i=0;i<buyButtons.length;i++)buyButtons[i].addEventListener("click",onBuyButton);function onBuyButton(o){o.preventDefault(),buyPopup.classList.add("modal-show")}buyClose.addEventListener("click",function(o){o.preventDefault(),buyPopup.classList.remove("modal-show")}),window.addEventListener("keydown",function(o){27===o.keyCode&&buyPopup.classList.contains("modal-show")&&(o.preventDefault(),buyPopup.classList.remove("modal-show"))});